package com.idea.base.core.dataSource;import java.util.List;import javax.sql.DataSource;import org.springframework.jdbc.core.support.JdbcDaoSupport;import org.springframework.jdbc.object.BatchSqlUpdate;public class JdbcService extends JdbcDaoSupport{	public  void insertRecords(String sql, final List<List<String>> list, final int[] types) {		DataSource ds = this.getDataSource();		BatchSqlUpdate bsu = new BatchSqlUpdate(ds, sql);		bsu.setBatchSize(100);		bsu.setTypes(types);		for(int i = 0; i < list.size(); i++){			int num = 0;			Object[] objects = new Object[types.length];			for(String str : list.get(i)){				str = str == null || str.length() == 0 ? "" : str;				objects[num] = str;				num++;			}			bsu.update(objects);		}		bsu.flush();	}}