package com.idea.base.core.dataSource;import java.util.List;import javax.sql.DataSource;import org.springframework.jdbc.core.support.JdbcDaoSupport;import org.springframework.jdbc.object.BatchSqlUpdate;public class JdbcService extends JdbcDaoSupport{	public void insertRecords(String sql, final List<List<String>> list, final int[] types) {		DataSource ds = this.getDataSource();		BatchSqlUpdate bsu = new BatchSqlUpdate(ds, sql);		bsu.setBatchSize(100);		bsu.setTypes(types);		for(int i = 0; i < list.size(); i++){			bsu.update(list.get(i));		}		bsu.flush();	}}